mapper.update:
update salary_system.t_notice


        title = #{title}


        content = #{content}


where notice_id = #{noticeId}

update salary_system.t_notice
set notice_status = #{status}
where notice_id = #{noticeId}

update salary_system.t_notice
set receiver_dep_id_list = #{originalReceiverDepIdList}
where notice_id = #{noticeId};

mapper.select:
select title,content,publisher_id,t1.create_time
from salary_system.t_notice t1 left join salary_system.t_notice_send  t2 on t1.notice_id = t2.notice_id


        notice_id = #{noticeId}


        publisher_id = #{publisherId}


        title like "%"#{title}"%"


        content like "%"#{content}"%"


        create_time between #{startDate} and #{endDate}

    t2.sender_status = 0

select notice_id
from salary_system.t_notice
where title = #{title}

select receiver_dep_id_list
from salary_system.t_notice
where notice_id = #{noticeId}
